package com.sanjeet.sessionmanagerimport kotlinx.coroutines.CoroutineScopeimport timber.log.Timberclass CrashHandler(    private val sessionManager: SessionManager,    private val applicationScope: CoroutineScope) {    init {        setUpCrashHandler()    }    private fun setUpCrashHandler() {        val defaultHandler = Thread.getDefaultUncaughtExceptionHandler()        Thread.setDefaultUncaughtExceptionHandler { thread, throwable ->            try {                Timber.e(">>>>>>>>>>>>Uncaught exception")                sessionManager.endSession(applicationScope)            } catch (e: Exception) {                Timber.e(">>>>>>>>>>>>>Error handling uncaught exception")            }            // Delegate to the original default handler            defaultHandler?.uncaughtException(thread, throwable)        }    }}