package com.sanjeet.sessionmanagerimport android.content.Contextimport android.net.ConnectivityManagerimport android.net.NetworkCapabilitiesimport com.google.gson.JsonArrayimport com.google.gson.JsonObjectimport com.sanjeet.sessionmanager.dataBase.SessionModelimport java.text.DateFormatimport java.text.SimpleDateFormatimport java.util.Calendarimport java.util.Localeobject Utils {    fun isNetworkAvailable(context: Context): Boolean {        val connectivityManager =            context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager        val activeNetwork = connectivityManager.activeNetwork ?: return false        val networkCapabilities =            connectivityManager.getNetworkCapabilities(activeNetwork) ?: return false        return networkCapabilities.hasCapability(NetworkCapabilities.NET_CAPABILITY_INTERNET)    }    fun getCurrentTimeStamp(): String {        val df: DateFormat = SimpleDateFormat("yyyy-MM-dd HH:mm:ss", Locale.getDefault())        return df.format(Calendar.getInstance().time)    }    fun convertSessionsToJsonArray(sessions: List<SessionModel>): JsonArray {        val jsonArray = JsonArray()        for (session in sessions) {            val sessionJson = JsonObject().apply {                addProperty("id", session.id)                addProperty("startTime", session.startTime)                addProperty("endTime", session.endTime)                addProperty("duration", session.duration)                addProperty("isOnline", session.isOnline)            }            jsonArray.add(sessionJson)        }        return jsonArray    }}