package com.sanjeet.sessionmanager.dataBaseimport android.content.Contextimport androidx.room.Roomobject DatabaseProvider {    private const val DATABASE_NAME = "session_database"    @Volatile    private var INSTANCE: SessionDatabase? = null    fun getDatabase(context: Context): SessionDatabase {        return INSTANCE ?: synchronized(this) {            val instance = Room.databaseBuilder(                context.applicationContext,                SessionDatabase::class.java,                DATABASE_NAME            ).build()            INSTANCE = instance            instance        }    }}