package com.sanjeet.sessionmanager.dataBaseimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface SessionRecordDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insert(sessionRecord: SessionModel)    @Query("SELECT * FROM session_records")    suspend fun getAllSessions(): List<SessionModel>    @Query("DELETE FROM session_records")    suspend fun deleteAllSessions()}